import React from 'react';

class Person extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            myName: '',
            myGender: 'Male',
            myHeight: 0,
            myWeight: 0,
            myAge: 0
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    changeName(event) {
        this.setState({
            myName: event.target.value
        });
    }

    changeGender(event) {
        this.setState({
            myGender: event.target.value
        });
    }

    changeHeight(event) {
        this.setState({
            myHeight: event.target.value
        });
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });
    }

    handleSubmit(event) {
        alert(this.state.myWeight);
        event.preventDefault();
    }

    render() {
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <h1>Sign Up!</h1>

                    <section className = "name">
                        <label>
                            Name:
                            <input name = "myName" type="text" value = {this.state.myName} onChange = {this.handleInputChange} />
                        </label>
                    </section>
                    <section className = "gender">
                        <label>
                            Gender:
                            <select name = "myGender" value = {this.state.myGender} onChange = {this.handleInputChange}>
                                <option value = "Male">Male</option>
                                <option value = "Female">Female</option>
                                {/* <option value = "Other">Other</option> */}
                            </select>
                         </label>
                    </section>
                    <section className = "height">
                        <label for='myHeight'>
                            Height (inches):
                            <input type="number" name = "myHeight" value = {this.state.myHeight} onChange = {this.handleInputChange} />
                        </label>
                    </section>
                    <section className = "weight">
                        <label for='myWeight'>Weight (pounds):
                            <input type="number" name="myWeight" value = {this.state.myWeight} onChange = {this.handleInputChange} />
                        </label>
                    </section>
                    <section className = "age">
                        <label for='myAge'>Age (years): </label>
                        <input type="number" name="myAge" id="myAge"></input>
                    </section>

                    <br />

                    <section class = "next">
                        <input type="submit" value="Next" />
                    </section>
                </form>
            </div>
        );
    }
}

export default Person;
