import React from 'react';

class Person extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            gender: '',
            height: 0,
            weight: 0,
            age: 0
        };

        this.changeName = this.changeName.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

     changeName(event) {
        this.setState({
            name: event.target.name
        });
    }

    handleSubmit(event) {
        // alert('A name was submitted: ' + this.state.value);
        // event.preventDefault();
        alert('A name was submitted: '+this.state.name)
        event.preventDefault();
    }

    render() {
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <h1>Sign Up!</h1>

                    <section className = "name">
                        <label>
                            Name:
                            <input type="text"  value = {this.state.name} onChange = {this.changeName} />
                        </label>
                    </section>

                    <section className = "gender">
                        <label for='myGender'>Gender: </label>
                        <input list="genders" name="myGender" id="myGender"></input>
                        <datalist id = 'genders'>
                            <option value = "Male"></option>
                            <option value = "Female"></option>
                            <option value = "Other"></option>
                         </datalist>
                    </section>

                    <section className = "height">
                        <label for='myHeight'>Height (inches): </label>
                        <input type="number" name="myHeight" id="myHeight"></input>
                    </section>

                    <section className = "weight">
                        <label for='myWeight'>Weight (pounds): </label>
                        <input type="number" name="myWeight" id="myWeight"></input>
                    </section>

                    <section className = "age">
                        <label for='myAge'>Age (years): </label>
                        <input type="number" name="myAge" id="myAge"></input>
                    </section>

                    <br />

                    <section class = "next">
                        <input type="submit" value="Next" />
                    </section>
                </form>
            </div>
        );
    }
}

export default Person;
